import Navbar from "./Navbar";
import React from "react";
import styles from "../css/Dashboard.module.css";
import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";

function Dashboard() {
  const navigate = useNavigate();
  const [image, setImage] = useState(null);
  const [loading, setLoading] = useState(false);
  const [type, setType] = useState(null);
  const [email, setEmail] = useState(null);
  const [username, setUsername] = useState(null);
  const [name, setName] = useState(null);
  const [title, setTitle] = useState(null);
  const [price, setPrice] = useState(null);
  const [productImg, setProductImg] = useState(null);
  const [quantity, setQuantity] = useState(null);
  const [description, setDescription] = useState(null);

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    setProductImg(file);
    if (file) {
      setLoading(true);
      const previewURL = URL.createObjectURL(file);
      setImage(previewURL);
    }
  };

  useEffect(() => {
    const checkSeller = async () => {
      const response = await fetch("http://localhost:5000/users/check-type", {
        method: "GET",
        credentials: "include",
        headers: { "Content-type": "application/json" },
      });
      const data = await response.json();
      console.log(data);
      if (data.message === "unauthorized") navigate("/");
      if (data.message) {
        setType(true);
      } else {
        setType(false);
      }
    };
    checkSeller();
  }, []);

  useEffect(() => {
    const userInfo = async () => {
      const response = await fetch(
        "http://localhost:5000/users/profile-details",
        {
          method: "GET",
          headers: { "Content-type": "application/json" },
          credentials: "include",
        }
      );
      const details = await response.json();

      setUsername(details.username);
      setEmail(details.email);
      setName(details.name);
    };
    userInfo();
  }, []);

  const addProduct = async (e) => {
    e.preventDefault();

    const formdata = new FormData();
    formdata.append("title", title);
    formdata.append("price", price);
    formdata.append("quantity", quantity);
    formdata.append("description", description);
    formdata.append("productImage", productImg);
    formdata.forEach((value, key) => {
      console.log(`${key}:`, value);
    });

    const response = await fetch("http://localhost:5000/products/add-product", {
      method: "POST",
      credentials: "include",
      body: formdata,
    });
    const result = await response.json();
    alert(result.message);
  };

  return (
    <>
      <Navbar />
      {type && (
        <div className={styles.dashboard_sect}>
          <div className={styles.add_product}>
            <form action="#" encType="multipart/form-data">
              <h3>Add Product</h3>
              <div className={styles.add_product_form}>
                <input
                  type="text"
                  placeholder="Enter title (Unique)"
                  maxLength={40}
                  onChange={(e) => setTitle(e.target.value)}
                  required
                />
                <div className={styles.uploader_container}>
                  <label
                    htmlFor="file-upload"
                    className={styles.custom_file_upload}
                  >
                    Upload Image
                  </label>
                  <input
                    id="file-upload"
                    type="file"
                    accept="image/*"
                    onChange={handleImageChange}
                    className={styles.file_input}
                    required
                  />

                  {loading && (
                    <div className={styles.spinner_container}>
                      <div className={styles.spinner}></div>
                      <p>Loading image...</p>
                    </div>
                  )}

                  {image && (
                    <img
                      src={image}
                      alt="Preview"
                      className={styles.image_preview}
                      onLoad={() => setLoading(false)}
                    />
                  )}
                </div>
                <input
                  type="number"
                  placeholder="Enter price"
                  onChange={(e) => setPrice(e.target.value)}
                  required
                />
                <input
                  type="number"
                  placeholder="Enter quantity"
                  onChange={(e) => setQuantity(e.target.value)}
                  required
                />
                <textarea
                  placeholder="Enter description"
                  className={styles.desc_input}
                  maxLength={200}
                  onChange={(e) => setDescription(e.target.value)}
                  required
                ></textarea>
              </div>
              <button className={styles.btn} onClick={addProduct}>
                Add
              </button>
            </form>
          </div>

          <div className={styles.dlt_product}>
            <form action="#">
              <h3>Delete Product</h3>
              <input type="text" placeholder="Enter title" required />
              <button className={styles.btn}>Delete</button>
            </form>
          </div>

          <div className={styles.payment_details}>
            <form action="#">
              <h3>Add Payment details</h3>
              <div className={styles.payment_details_form}>
                <input type="text" placeholder="Card number" required />
                <input type="text" placeholder="Expiry date" required />
                <input type="number" placeholder="CVC" required />
                <input type="text" placeholder="Card holder name" required />
              </div>
              <button className={styles.btn}>Add</button>
            </form>
          </div>

          <div className={styles.user_info}>
            <h3>Account Information</h3>
            {username !== null && (
              <p>
                <strong>Username: </strong> {username ? username : "Loading"}
              </p>
            )}
            {email !== null && (
              <p>
                <strong>Email: </strong> {email ? email : "Loading"}
              </p>
            )}
            {name !== null && (
              <p>
                <strong>Name: </strong> {name ? name : "Loading"}
              </p>
            )}
            {type !== null && (
              <p>
                <strong>Account Type: </strong> {type ? "Seller" : "Buyer"}
              </p>
            )}
            <form action="#" className={styles.user_info_form}>
              <input type="password" placeholder="Old Password" />
              <input type="password" placeholder="New Password" />
              <button className={styles.btn}>Change</button>
            </form>
          </div>
        </div>
      )}
    </>
  );
}

export default Dashboard;
