import Navbar from "./Navbar";
import React from "react";
import styles from "../css/Dashboard.module.css";
import { useState } from "react";

function Dashboard() {
  const [image, setImage] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setLoading(true);
      const previewURL = URL.createObjectURL(file);
      setImage(previewURL);
    }
  };
  return (
    <>
      <Navbar />
      <div className={styles.dashboard_sect}>
        <div className={styles.add_product}>
          <form action="#">
            <h3>Add Product</h3>
            <div className={styles.add_product_form}>
              <input
                type="text"
                placeholder="Enter title (Unique)"
                maxLength={40}
                required
              />
              <div className={styles.uploader_container}>
                <label
                  htmlFor="file-upload"
                  className={styles.custom_file_upload}
                >
                  Upload Image
                </label>
                <input
                  id="file-upload"
                  type="file"
                  accept="image/*"
                  onChange={handleImageChange}
                  className={styles.file_input}
                  required
                />

                {loading && (
                  <div className={styles.spinner_container}>
                    <div className={styles.spinner}></div>
                    <p>Loading image...</p>
                  </div>
                )}

                {image && (
                  <img
                    src={image}
                    alt="Preview"
                    className={styles.image_preview}
                    onLoad={() => setLoading(false)}
                  />
                )}
              </div>
              <input type="number" placeholder="Enter price" required />
              <input type="number" placeholder="Enter quantity" required />
              <textarea
                placeholder="Enter description"
                className={styles.desc_input}
                maxLength={200}
                required
              ></textarea>
            </div>
            <button>Add</button>
          </form>
        </div>

        <div className={styles.dlt_product}>
          <form action="#">
            <input type="text" />
          </form>
        </div>
      </div>
    </>
  );
}

export default Dashboard;
