import Navbar from "./Navbar";
import React from "react";
import styles from "../css/Dashboard.module.css";
import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";

function Dashboard() {
  const navigate = useNavigate();
  const [image, setImage] = useState(null);
  const [loading, setLoading] = useState(false);
  const [type, setType] = useState(null);
  const [email, setEmail] = useState(null);
  const [username, setUsername] = useState(null);
  const [name, setName] = useState(null);

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setLoading(true);
      const previewURL = URL.createObjectURL(file);
      setImage(previewURL);
    }
  };

  useEffect(() => {
    const checkSeller = async () => {
      const response = await fetch("http://localhost:5000/users/check-type", {
        method: "GET",
        credentials: "include",
        headers: { "Content-type": "application/json" },
      });
      const data = await response.json();
      console.log(data);
      if (data.message === "unauthorized") navigate("/");
      if (data.message) {
        setType(true);
        console.log("Type value: ", type);
      } else {
        setType(false);
        console.log("Type value: ", type);
      }
    };
    checkSeller();
  }, []);

  useEffect(() => {
    const userInfo = () => {};
  }, []);

  return (
    <>
      <Navbar />
      {type && (
        <div className={styles.dashboard_sect}>
          <div className={styles.add_product}>
            <form action="#">
              <h3>Add Product</h3>
              <div className={styles.add_product_form}>
                <input
                  type="text"
                  placeholder="Enter title (Unique)"
                  maxLength={40}
                  required
                />
                <div className={styles.uploader_container}>
                  <label
                    htmlFor="file-upload"
                    className={styles.custom_file_upload}
                  >
                    Upload Image
                  </label>
                  <input
                    id="file-upload"
                    type="file"
                    accept="image/*"
                    onChange={handleImageChange}
                    className={styles.file_input}
                    required
                  />

                  {loading && (
                    <div className={styles.spinner_container}>
                      <div className={styles.spinner}></div>
                      <p>Loading image...</p>
                    </div>
                  )}

                  {image && (
                    <img
                      src={image}
                      alt="Preview"
                      className={styles.image_preview}
                      onLoad={() => setLoading(false)}
                    />
                  )}
                </div>
                <input type="number" placeholder="Enter price" required />
                <input type="number" placeholder="Enter quantity" required />
                <textarea
                  placeholder="Enter description"
                  className={styles.desc_input}
                  maxLength={200}
                  required
                ></textarea>
              </div>
              <button className={styles.btn}>Add</button>
            </form>
          </div>

          <div className={styles.dlt_product}>
            <form action="#">
              <h3>Delete Product</h3>
              <input type="text" placeholder="Enter title" required />
              <button className={styles.btn}>Delete</button>
            </form>
          </div>

          <div className={styles.payment_details}>
            <form action="#">
              <h3>Add Payment details</h3>
              <div className={styles.payment_details_form}>
                <input type="text" placeholder="Card number" required />
                <input type="text" placeholder="Expiry date" required />
                <input type="number" placeholder="CVC" required />
                <input type="text" placeholder="Card holder name" required />
              </div>
              <button className={styles.btn}>Add</button>
            </form>
          </div>

          <div className={styles.user_info}>
            <h3>Account Information</h3>
            <p>
              <strong>Username: </strong>
              <span></span>
            </p>
            <p>
              <strong>Email: </strong>
              <span></span>
            </p>
            <p>
              <strong>Name: </strong>
              <span></span>
            </p>
            <p>
              <strong>Account Type: </strong>
              {type !== null && <p>User Type: {type ? "Seller" : "Buyer"}</p>}
              <span>{type}</span>
            </p>
            <form action="#" className={styles.user_info_form}>
              <input type="password" placeholder="Old Password" />
              <input type="password" placeholder="New Password" />
              <button className={styles.btn}>Change</button>
            </form>
          </div>
        </div>
      )}
    </>
  );
}

export default Dashboard;
