import Navbar from "./Navbar";
import hubaib from "../images/hubaib.jpg";
import hussain from "../images/hussain.jpeg";
import styles from "../css/Cart.module.css";
import React from "react";
import { useNavigate } from "react-router-dom";
import { useState, useEffect } from "react";

function Cart() {
  const navigate = useNavigate();
  const [retProducts, setRetProducts] = useState([]);
  const [total, setTotal] = useState(0);
  const [quantity, setQuantity] = useState([]);

  useEffect(() => {
    const apiCall = async () => {
      const getProducts = await fetch(
        "http://localhost:5000/cart/get-cart-products",
        {
          method: "GET",
          credentials: "include",
        }
      );
      const data = await getProducts.json();
      console.log(data);
      setRetProducts(data.cart);
    };
    apiCall();
  }, []);

  useEffect(() => {
    const totalPrice = retProducts.reduce(
      (sum, product) => sum + (product.price || 0),
      0
    );
    setTotal(totalPrice);
  }, [retProducts]);

  const handleQuantity = (id, q, price) => {
    setQuantity((prev) => ({
      ...prev,
      [id]: q * price,
    }));
  };

  const handleCheckout = () => {
    console.log("Quantity", quantity);
  };

  return (
    <>
      <Navbar />
      <div className={styles.cartPage}>
        <div className={styles.items_section}>
          {retProducts &&
            retProducts.map((product) => (
              <div className={styles.item} key={product._id}>
                <img
                  src={`http://localhost:5000/productImages/${product.imageURL}`}
                  alt=""
                />
                <div>
                  <h5>
                    <strong>{product.title}</strong>
                  </h5>
                  <p>
                    <strong>Price (pkr): {product.price}</strong>
                  </p>
                  <p>
                    <strong>
                      Available quantity: {product.productQuantity}
                    </strong>
                  </p>
                  <p>
                    <strong>Description: </strong>
                  </p>
                  <p>{product.description}</p>
                  <strong>Quantity: </strong>
                  <input
                    type="number"
                    placeholder="Enter quantity"
                    value="0"
                    onChange={(e) =>
                      handleQuantity(product._id, e.target.value, product.price)
                    }
                    required
                  />
                </div>
              </div>
            ))}
        </div>
        <div>
          <button className={styles.totalBtn}>
            <strong>Total: {total}</strong>
          </button>
          <button className={styles.checkoutBtn} onClick={handleCheckout}>
            <strong>Checkout</strong>
          </button>
        </div>
      </div>
    </>
  );
}

export default Cart;
